import R from 'ramda'

import {
    FECTH_USER_BY_ID_SUCCESS
} from 'action-types/users'

const initialState = {
    id: null
}


export default (state = initialState, {type,payload}) => {
    switch(type){
        case FECTH_USER_BY_ID_SUCCESS:
            return R.merge(state,{
                id: R.prop('id', payload)
            })
            default: 
            return state
    }
}