import R from 'ramda'
import {
    FECTH_USERS_SUCCESS,
    FECTH_USER_BY_ID_SUCCESS
} from 'action-types/users'

const initialState = {}

export default (state = initialState, {type, payload}) => {
    switch(type){
        case FECTH_USERS_SUCCESS:
            const newValue = R.indexBy(R.prop('id'),payload)
            return R.merge(state, newValue)
        
        case FECTH_USER_BY_ID_SUCCESS:
            return R.assoc(payload.id, payload, state)
        
        default:
            return state
    }
}