import R from 'ramda'

import {
    FECTH_USERS_SUCCESS
} from 'action-types/users'

const initialState = {
    ids: []
}

export default (state = initialState, {type, payload}) => {
    switch(type){
        case FECTH_USERS_SUCCESS:
            return R.merge(state, {
              ids: R.pluck('id', payload)  
            })
        default:
        return state    
    }
}